import { useState, useEffect } from "react";
import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Table, TableHead, TableRow, TableCell, TableBody } from "@/components/ui/table";
import { toast } from "react-hot-toast";

const mockData = [
  { id: 1, name: "张三", date: "2025-03-25", time: "09:00" },
  { id: 2, name: "李四", date: "2025-03-25", time: "09:05" },
];

export default function Attendance() {
  const [email, setEmail] = useState("");
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [attendance, setAttendance] = useState(mockData);
  const [currentUser, setCurrentUser] = useState(null);
  const [currentDate, setCurrentDate] = useState(new Date().toLocaleDateString());

  const handleLogin = () => {
    if (email.trim()) {
      setCurrentUser(email);
      setIsLoggedIn(true);
    } else {
      toast.error("请输入有效的邮箱");
    }
  };

  const handleClockIn = () => {
    const now = new Date();
    const time = now.toLocaleTimeString();
    setAttendance([...attendance, { id: attendance.length + 1, name: currentUser, date: currentDate, time }]);
    toast.success("打卡成功！");
  };

  return (
    <div className="flex flex-col items-center mt-10 bg-white min-h-screen text-black">
      {!isLoggedIn ? (
        <Card className="p-6 w-96 border-2 border-[#FFDE17] shadow-lg">
          <CardContent>
            <h2 className="text-xl font-bold mb-4 text-[#FFDE17]">实习生登录</h2>
            <Input type="email" placeholder="输入邮箱" value={email} onChange={(e) => setEmail(e.target.value)} className="border border-[#FFDE17] focus:ring-[#FFDE17]" />
            <Button className="mt-4 w-full bg-[#FFDE17] text-black hover:bg-yellow-300" onClick={handleLogin}>登录</Button>
          </CardContent>
        </Card>
      ) : (
        <div className="w-96">
          <Card className="p-6 mb-6 border-2 border-[#FFDE17] shadow-md">
            <CardContent>
              <h2 className="text-xl font-bold text-[#FFDE17]">{currentUser}，欢迎</h2>
              <p className="mt-2">今天是：{currentDate}</p>
              <Button className="mt-4 w-full bg-[#FFDE17] text-black hover:bg-yellow-300" onClick={handleClockIn}>点击打卡</Button>
            </CardContent>
          </Card>
          <Card className="border-2 border-[#FFDE17]">
            <CardContent>
              <h2 className="text-lg font-bold mb-4 text-[#FFDE17]">打卡记录</h2>
              <Table>
                <TableHead>
                  <TableRow className="bg-[#FFDE17] text-black">
                    <TableCell>姓名</TableCell>
                    <TableCell>日期</TableCell>
                    <TableCell>时间</TableCell>
                  </TableRow>
                </TableHead>
                <TableBody>
                  {attendance.map((record) => (
                    <TableRow key={record.id}>
                      <TableCell>{record.name}</TableCell>
                      <TableCell>{record.date}</TableCell>
                      <TableCell>{record.time}</TableCell>
                    </TableRow>
                  ))}
                </TableBody>
              </Table>
            </CardContent>
          </Card>
        </div>
      )}
    </div>
  );
}
